import React from 'react';
import './App.css';
import Navigation from '../components/Navigation/Navigation'


class App extends Component {

  constructor(){
    super();
    this.state={
      input: '',
      imgUrl: '',
      box: '',
      route: 'signin',
      isSignedIn: false,
      user:{
        id: '',
        name: '',
        email: '',
        entries: 0,
        joined: ''
      }
    }
  }

  loadUser = (data) => {
    this.setState({
      user:{
        id: data.id,
        name: data.name,
        email: data.email,
        entries: data.entries,
        joined: data.joined
      }
    })
  }

  onInputChange = (event) => {
    this.setState({input: event.target.value});
  }

  drawBox = (boxLocation) => {
    this.setState({box: boxLocation});
    console.log(this.state.box);
  }

  onPictureSubmit = () => {
    this.setState({imgUrl: this.state.input});
    app.models.predict(Clarifai.FACE_DETECT_MODEL, this.state.input)
    .then( response => {
      if (response){
        fetch('http://localhost:3000/image', {
          method: 'put',
          headers: {'Content-Type': 'application/json'},
          body: JSON.stringify({
            id: this.state.user.id
          })
        })
        .then(response => response.json())
        .then(count => {
          this.setState(Object.assign(this.state.user,{entries: count}))
        })
      }
      this.drawBox(calculateFaceLocation(response))
    })
    .catch(err => console.log(err));
  }

  changeRoute = (route) => {
    if (route === 'signout'){
      this.setState({isSignedIn: false})
    } else if (route === 'home'){
      this.setState({isSignedIn: true})
    }
    this.setState({route: route})
  }

  render(){
    return (
      <div className='App'>
        <Particles 
          className='particles' 
          params={particlesOptions}
        />               
        <Navigation isSignIn={this.state.isSignedIn} changeRoute={this.changeRoute}/>
        { this.state.route === 'signin'?
          <SignIn loadUser={this.loadUser} changeRoute={this.changeRoute}/>:
          (
          this.state.route === 'home'?
          <div>
            <Logo />
            <Rank name={this.state.user.name} entries={this.state.user.entries}/>
            <ImageLinkForm onInputChange={this.onInputChange} onSubmit={this.onPictureSubmit}/>       
            <FaceRecognition imgUrl={this.state.imgUrl} box={this.state.box}/>
          </div>:
            <Register changeRoute={this.changeRoute} loadUser={this.loadUser}/>)

        }               
      </div>
    )
  }
}

export default App;
